#!/bin/bashfor i in `docker ps | grep Up | awk  '{print $1}'`do        echo &&docker        top $idoneecho "为当前系统加载一个doceker镜像"docker run  -i -d  -P iso.docker/ubuntu:14.01 /bin/bashdocker images > virtual_images   #docker仓库的全部镜像text_code=`cat virtual_images | grep  ubuntu:14.01` 	 #测试if [ $text_code -eq 1  ]then	echo "docker仓库有ubuntu14:01这个镜像"	docker  ps 			#列出这个镜像的进程else	docker pull ubuntu:14.01 		#从公共仓库里下载这个镜像fiservice docker status  			#获取当前的docker状态#==============awk,find,grep,sed四剑客================`#!/bin/bashstr=`find ./1.txt  -name "*.txt" -print >> 1.txt` #定义一段文本用作测试sed  -n -e '1,2' p'  > 1.txt.seqgrep ^a  1.rxt.seq > 1.txt.seq.grep\. ./2.sh#!/bin/awk -f$1 > 10 {	print }